    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarActionPerformed
        
        //ENTRADA
        int autor_id =Integer.parseInt(txtAutorId.getText());
        String nombre = txtNombre.getText();
        char sexo = cboSexo.getSelectedItem().toString().charAt(0);
        
        // Obtener la fecha del JDateChoose
         Date fechaDate = dchFnacimiento.getDate();
          LocalDate fnacimiento = fechaDate.toInstant()
                                        .atZone(ZoneId.systemDefault())
                                        .toLocalDate();
          
         
        String nacionalidad = cboNacionalidad.getSelectedItem().toString();
        double patrimonio = Double.parseDouble(txtPatrimonio.getText());
        
        
        //PROCESO
        //instancia -> objeto(autor)
        Autor autor=new Autor(autor_id, nombre, sexo, fnacimiento, nacionalidad, patrimonio);
        
        
        //SALIDA
        autor.detalle(txaImprimir);
        
        
    }//GEN-LAST:event_btnGuardarActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        txtAutorId.setText(null);
        txtNombre.setText(null);
        cboSexo.setSelectedIndex(0);
        dchFnacimiento.setCalendar(null);
        cboNacionalidad.setSelectedIndex(0);
        txtPatrimonio.setText(null);
        
        txtAutorId.requestFocus();
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

