    private void btnEnviarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEnviarActionPerformed
        //ENTRADA
        double sueldo= Double.parseDouble(txtSueldo.getText());
        String sexo= txtSexo.getText();
        String eCivil= txtECivil.getText();
        
        System.out.println(sueldo);
        System.out.println(sexo);
        System.out.println(eCivil);
        
        //PROCESO
        this.calcularBonificacion(sexo, eCivil,sueldo);
        this.calcularDescuento(sexo,eCivil,sueldo,bonificacion);
        this.calcularPagoTotal(descuento,bonificacion,sueldo);
            
        //SALIDA
        txtImprimir.setText(null);
        txtImprimir.append("Bonificacion: S/"+bonificacion);
        txtImprimir.append("\nDescuento: S/"+descuento);
        txtImprimir.append("\nPago Total: S/"+pago_total);

    }//GEN-LAST:event_btnEnviarActionPerformed

     //procedimiento
    public void calcularBonificacion(String sexo, String eCivil,double sueldo)
    {
        if (sexo.equals("Masculino") && eCivil.equals("Soltero")) {
           bonificacion = sueldo * 0.08;
       } else if (sexo.equals("Femenino") && eCivil.equals("Soltero")) {
           bonificacion = sueldo * 0.1;
       } else if (sexo.equals("Masculino") && eCivil.equals("Casado")) {
           bonificacion = sueldo * 0.1;
       } else if (sexo.equals("Femenino") && eCivil.equals("Casado")) {
           bonificacion = sueldo * 0.12;
       }
    }
    
    //procedimiento
    public void calcularDescuento(String sexo, String eCivil,double sueldo,double bonificacion)
    {
        descuento= sueldo+bonificacion;
        
        if(sexo.equals("Masculino") && eCivil.equals("Soltero"))
       {
           descuento = descuento *0.06;
       }else if(sexo.equals("Femenino") && eCivil.equals("Soltero")) 
       {
            descuento = descuento *0.05;
       }else if(sexo.equals("Masculino") && eCivil.equals("Casado"))
       {
            descuento = descuento *0.04;
       }else if(sexo.equals("Femenino") && eCivil.equals("Casado"))
       {
            descuento = descuento *0.03;
       }
        
    }
    
    public void calcularPagoTotal(double descuento, double bonificacion,double sueldo)
    {
        pago_total=descuento+bonificacion+sueldo;
    }
    
    
